    private void loginbActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loginbActionPerformed
        // TODO add your handling code here:
        if((rolecombo.getSelectedIndex()==-1)){
            JOptionPane.showMessageDialog(null, "Please select your role");
        }
       if(!(rolecombo.getSelectedIndex()==-1)){
        try{
            String role;
            role=rolecombo.getSelectedItem().toString();
            if(role=="Customer"){
                Books b=new Books();
                FeedBack f=new FeedBack();
                b.setusername(untext.getText());
                f.setusername(untext.getText());
                    user.checkCust(untext.getText(), ptext.getText(), role, this);}
        else if(role=="Admin"){
            Admin admin=new Admin();
                                    admin.checkAdmin(untext.getText(), ptext.getText(), role, this);
        ManageBooks mb=new ManageBooks();
        mb.setusername(untext.getText());
        }}catch(NullPointerException e){
            JOptionPane.showMessageDialog(null, e.getMessage());
            e.printStackTrace();
        }}
    }//GEN-LAST:event_loginbActionPerformed

